% Computer exercise 1.7% From exercise 3.1R=[2 1; 1 2];  % correlation matrix of the tap-input vectorp=[6; 4];      % cross-correlation vector between tap-input vector               %  and desired signalJmin=5;        % minimum mean square errorwinit=[0;0];   % initial weightsN=50;          % number of iterations%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%wopt=R\p;      % optimum Wiener filter solution[V,L]=eig(R);            % eigenvector V=[v1 v2] and eigenvalues of Rlambda=diag(L);          % 2x1 vector containing the eigenvaluesw1=[(wopt(1)-4):0.1:(wopt(1)+4)];w2=[(wopt(2)-4):0.1:(wopt(2)+4)];Jm=jmat(w1,w2,R,p,Jmin);figure surf(w1,w2,Jm); xlabel('w_1'); ylabel('w_2'); zlabel('J(w_1,w_2)'); title('CE 1.7');figure contour(w1,w2,Jm,50); hold on plot(wopt(1),wopt(2),'k-o'); text(wopt(1)+.1,wopt(2)+.1,'w_o_p_t'); xlabel('w_1'); ylabel('w_2'); colorbar; title('CE 1.7');