% Computer exercise 1.8% From exercise 3.1R=[2 1; 1 2];  % correlation matrix of the tap-input vectorp=[6; 4];      % cross-correlation vector between tap-input vector               %  and desired signalJmin=5;        % minimum mean square errorwinit=[0;0];   % initial weightsN=50;          % number of iterations%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%wopt=R\p;      % optimum Wiener filter solution[V,L]=eig(R);            % eigenvector V=[v1 v2] and eigenvalues of Rlambda=diag(L);          % 2x1 vector containing the eigenvaluesa=3;winita=wopt+a*[1;1];winitb=wopt+a*[1;-1];w1=[(wopt(1)-4):0.1:(wopt(1)+4)];w2=[(wopt(2)-4):0.1:(wopt(2)+4)];Jm=jmat(w1,w2,R,p,Jmin);[wa,Ja]=sd(2/3-0.01,winita,N,R,p,Jmin);[wb,Jb]=sd(1,winitb,N,R,p,Jmin);figure contour(w1,w2,Jm,50); xlabel('w_1'); ylabel('w_2'); colorbar; hold on plot(wa(1,:),wa(2,:),'b-x'); plot(wb(1,:),wb(2,:),'r-+'); legend('J(w1,w2)','starting on the shorter axis (\lambda=3), \mu=2/3-0.01',...        'starting on the longer axis (\lambda=1), \mu=1 (>2/3 !!)') zoom on title('CE 1.9');