% Computer exercise 1.3% From exercise 3.1R=[2 1; 1 2];  % correlation matrix of the tap-input vectorp=[6; 4];      % cross-correlation vector between tap-input vector               %  and desired signalJmin=5;        % minimum mean square errorwinit=[0;0];   % initial weightsN=50;          % number of iterations%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%%wopt=R\p;      % optimum Wiener filter solution[V,L]=eig(R);            % eigenvector V=[v1 v2] and eigenvalues of Rlambda=diag(L);          % 2x1 vector containing the eigenvaluesmu=2/max(lambda)/2;      % step sizev=V'*(winit-wopt);       % transformation to natural modes% MSE, analytical solution obtained in exercise 3.1cJanalyt=Jmin+2*(4/9).^(0:N-1);% weights and MSE using sd[w,J]=sd(mu,winit,N,R,p,Jmin);% since in MATLAB 0^0=1, sd calculates theJanalytm=Jmin+2*(4/9).^(0:N-1)+...              [lambda(2)*abs(v(2))^2 zeros(1,N-1)].*[1 zeros(1,N-1)];figure plot(0:N-1,J,'b'); hold on; plot(0:N-1,Janalyt,'g'); plot(0:N-1,Janalytm,'r--'); grid on zoom on title('CE 1.3'); text(5,15,{'The devation for n=0 stems from the term 0^0*|\nu_1(0)|^2,',...         'which MATLAB evaluates to be 1*100/6 due to its convention 0^0=1.'}); legend('J(n), calculated with sd.m^{ }',...        'J(n), analytical solution^{ }',...        'J(n), Matlab''s analytical solution using 0^0=1'); xlabel('iteration n'); ylabel('MSE J(n)');